import { makeStyles, useTheme, Theme, createStyles } from '@material-ui/core/styles';

export const useStylesOrders = makeStyles((theme: Theme) => {
    return (
    createStyles({
      root: {
        padding: theme.spacing(3),
      }
    }))
});


/*==================================*/
export const colors = {
  "processing": {
    '--background-start': '#red',
    '--background-end': '#21CBF3',
    '--box-shadow': 'rgba(33, 203, 243, .3)',
  } as React.CSSProperties,
  "completed": {
    '--background-start': '#FE6B8B',
    '--background-end': '#FF8E53',
    '--box-shadow': 'rgba(255, 105, 135, .3)',
  } as React.CSSProperties
}

export const useStylesStatusSelector = makeStyles((theme: Theme) => {
  return (
    createStyles ({
        filled: {
          background: 'var(--background-start)',
          borderRadius: 10,
          border: 0,
          boxShadow: '0 3px 5px 2px var(--box-shadow)',
        }
    })
  )
})
