import React, {useContext, useEffect, useState} from 'react';
import axios from 'axios';
import ProductList from './OrderList';
import ProductItem from './OrderItem';
import CircularProgress from '@material-ui/core/CircularProgress';
import Fade from '@material-ui/core/Fade';
import useStylesOrders from './Orders.style';

type OrdersProps = {
    orders:any
}

const OrdersFade: React.FC<OrdersProps> = (props) => {

    return (
        <div {...props} >
            <ProductList>
                {props.orders.map((order) => {
                    return (
                    <ProductItem 
                        key = {order.id}
                        order={order}
                    />)}
                )}
            </ProductList>
            )}
        </div>
    )
}

const OrdersPage: React.FC = () => {
    const [orders, setOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const classes = useStylesOrders();

    useEffect(() => {
        async function loadOrders() {
            const newOrders = await axios.get("http://localhost:3000/api/orders");
            setOrders(newOrders.data);
            setIsLoading(false);
        }
        loadOrders();
    }, []);

    return (
        <div className = {classes.root}>
            <h1>Commandes</h1>
            {isLoading == true && <CircularProgress />}
            <Fade in={!isLoading} timeout={500}>
                <OrdersFade orders={orders}/>
            </Fade>
        </div>
    )
}
export default OrdersPage;
