import React from 'react';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import StatusSelector from './StatusSelector';

type OrderItemProps = {
    order: any
}

const OrderItem: React.FC<OrderItemProps> = (props) => {
    return (
        <TableRow key={props.order.id}>
            <IdCell>{props.order.id}</IdCell>
            <NameCell>{props.order.billing.first_name} {props.order.billing.last_name} </NameCell>
            <PriceCell>{props.order.total}€</PriceCell>
            <StatusCell>{props.order.status}</StatusCell>
            <MethodCell>{props.order.shipping_lines[0].method_title}</MethodCell>
        </TableRow>
    );
}

const IdCell: React.FC = (props) => {
    return (
        <TableCell>{props.children}</TableCell>
    );
}

const NameCell: React.FC = (props) => {
    return (
        <TableCell>{props.children}</TableCell>
    );
}

const PriceCell: React.FC = (props) => {
    return (
        <TableCell>{props.children}</TableCell>
    );
}

const StatusCell: React.FC = (props) => {
    let color = "white";
    let text = ""
    if (props.children.toString() == "processing"){
        color = "Coral";
        text = "En cours";
    }
    else if (props.children.toString() == "completed"){
        color = "LightGreen";
        text = "Terminée";
    }
    else if (props.children.toString() == "cancelled"){
        color = "WhiteSmoke";
        text = "Annulée";
    }
    else if (props.children.toString() == "refunded"){
        color = "#bad095";
        text = "Remboursée";
    }
    else {
        color = "White";
        text = props.children.toString();
    }
    return (
        <TableCell style={{backgroundColor:`${color}`,  borderRadius:10}}>{text}</TableCell>
    );
}

const MethodCell: React.FC = (props) => {
    return (
        <TableCell>{props.children}</TableCell>
    );
}

export default OrderItem;
