import React, {useContext, useEffect, useState} from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import useStylesStatusSelector from './Orders.style';

type StatusSelectorProps = {
    status: string,
    handleChange
}
const StatusSelector:React.FC<StatusSelectorProps> = (props) => {
    const classes = useStylesStatusSelector();
    return (

        <Select
        value={props.status}
        onChange={props.handleChange}
        variant="filled"
        >
        <MenuItem value="cancelled" classes={{selected: classes.menuitem.selected}}>Annulée</MenuItem>
        <MenuItem value="processing">En Cours</MenuItem>
        <MenuItem value="completed">Terminée</MenuItem>
        <MenuItem value="refunded">Remboursée</MenuItem>
        </Select>
    );
}

export default StatusSelector;
